// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.12;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

import "../BaseStrategyUpgradeable.sol";

import "../../interfaces/IGenericLender.sol";

/// @title OptimizerAPRGreedyStrategy
/// @author Forked from https://github.com/Grandthrax/yearnV2-generic-lender-strat
/// @notice A lender optimisation strategy for any ERC20 asset
/// @dev This strategy works by taking plugins designed for standard lending platforms
/// It automatically chooses the best yield generating platform and adjusts accordingly
/// The adjustment is sub optimal so there is an additional option to manually set position
contract OptimizerAPRGreedyStrategy is BaseStrategyUpgradeable {
    using SafeERC20 for IERC20;
    using Address for address;

    // ================================= REFERENCES ================================

    IGenericLender[] public lenders;

    // ================================= PARAMETERS ================================

    uint256 public withdrawalThreshold;

    // =================================== EVENTS ==================================

    event AddLender(address indexed lender);
    event RemoveLender(address indexed lender);

    // ================================ CONSTRUCTOR ================================

    /// @notice Constructor of the `Strategy`
    /// @param _poolManager Address of the `PoolManager` lending to this strategy
    /// @param governor Address with governor privilege
    /// @param guardian Address of the guardian
    function initialize(
        address _poolManager,
        address governor,
        address guardian,
        address[] memory keepers
    ) external {
        _initialize(_poolManager, governor, guardian, keepers);
        withdrawalThreshold = 1000 * wantBase;
    }

    // ============================= INTERNAL MECHANICS ============================

    /// @notice Frees up profit plus `_debtOutstanding`.
    /// @param _debtOutstanding Amount to withdraw
    /// @return _profit Profit freed by the call
    /// @return _loss Loss discovered by the call
    /// @return _debtPayment Amount freed to reimburse the debt
    /// @dev If `_debtOutstanding` is more than we can free we get as much as possible.
    function _prepareReturn(uint256 _debtOutstanding)
        internal
        override
        returns (
            uint256 _profit,
            uint256 _loss,
            uint256 _debtPayment
        )
    {
        _profit = 0; //default assignments for clarity
        _loss = 0;
        _debtPayment = _debtOutstanding;

        uint256 lentAssets = lentTotalAssets();

        uint256 looseAssets = want.balanceOf(address(this));

        uint256 total = looseAssets + lentAssets;

        if (lentAssets == 0) {
            // No position to harvest or profit to report
            if (_debtPayment > looseAssets) {
                // We can only return looseAssets
                _debtPayment = looseAssets;
            }

            return (_profit, _loss, _debtPayment);
        }

        uint256 debt = poolManager.strategies(address(this)).totalStrategyDebt;

        if (total > debt) {
            _profit = total - debt;

            uint256 amountToFree = _profit + _debtPayment;
            // We need to add outstanding to our profit
            // don't need to do logic if there is nothing to free
            if (amountToFree != 0 && looseAssets < amountToFree) {
                // Withdraw what we can withdraw
                _withdrawSome(amountToFree - looseAssets);
                uint256 newLoose = want.balanceOf(address(this));

                // If we dont have enough money adjust _debtOutstanding and only change profit if needed
                if (newLoose < amountToFree) {
                    if (_profit > newLoose) {
                        _profit = newLoose;
                        _debtPayment = 0;
                    } else {
                        _debtPayment = Math.min(newLoose - _profit, _debtPayment);
                    }
                }
            }
        } else {
            // Serious loss should never happen but if it does lets record it accurately
            _loss = debt - total;

            uint256 amountToFree = _loss + _debtPayment;
            if (amountToFree != 0 && looseAssets < amountToFree) {
                // Withdraw what we can withdraw

                _withdrawSome(amountToFree - looseAssets);
                uint256 newLoose = want.balanceOf(address(this));

                // If we dont have enough money adjust `_debtOutstanding` and only change profit if needed
                if (newLoose < amountToFree) {
                    if (_loss > newLoose) {
                        _loss = newLoose;
                        _debtPayment = 0;
                    } else {
                        _debtPayment = Math.min(newLoose - _loss, _debtPayment);
                    }
                }
            }
        }
    }

    /// @notice Estimates highest and lowest apr lenders among a `lendersList`
    /// @param lendersList List of all the lender contracts associated to this strategy
    /// @return _lowest The index of the lender in the `lendersList` with lowest apr
    /// @return _lowestApr The lowest apr
    /// @return _highest The index of the lender with highest apr
    /// @return _potential The potential apr of this lender if funds are moved from lowest to highest
    /// @dev `lendersList` is kept as a parameter to avoid multiplying reads in storage to the `lenders`
    /// array
    function _estimateAdjustPosition(IGenericLender[] memory lendersList)
        internal
        view
        returns (
            uint256 _lowest,
            uint256 _lowestApr,
            uint256 _highest,
            uint256 _potential
        )
    {
        // All loose assets are to be invested
        uint256 looseAssets = want.balanceOf(address(this));

        // Simple algo:
        //  - Get the lowest apr strat
        //  - Cycle through and see who could take its funds plus want for the highest apr
        _lowestApr = type(uint256).max;
        _lowest = 0;
        uint256 lowestNav = 0;
        uint256 highestApr = 0;
        _highest = 0;
        uint256 length = lendersList.length;
        for (uint256 i = 0; i < length; i++) {
            uint256 aprAfterDeposit = lendersList[i].aprAfterDeposit(int256(looseAssets));
            if (aprAfterDeposit > highestApr) {
                highestApr = aprAfterDeposit;
                _highest = i;
            }

            if (lendersList[i].hasAssets()) {
                uint256 apr = lendersList[i].apr();
                if (apr < _lowestApr) {
                    _lowestApr = apr;
                    _lowest = i;
                    lowestNav = lendersList[i].nav();
                }
            }
        }

        //if we can improve apr by withdrawing we do so
        _potential = lendersList[_highest].aprAfterDeposit(int256(lowestNav + looseAssets));
    }

    /// @notice Function called by keepers to adjust the position
    /// @dev The algorithm moves assets from lowest return to highest
    /// like a very slow idiot bubble sort
    function _adjustPosition() internal override {
        // Emergency exit is dealt with at beginning of harvest
        if (emergencyExit) {
            return;
        }
        // Storing the `lenders` array in a cache variable
        IGenericLender[] memory lendersList = lenders;
        // We just keep all money in want if we dont have any lenders
        if (lendersList.length == 0) {
            return;
        }

        (uint256 lowest, uint256 lowestApr, uint256 highest, uint256 potential) = _estimateAdjustPosition(lendersList);

        if (potential > lowestApr) {
            // Apr should go down after deposit so won't be withdrawing from self
            lendersList[lowest].withdrawAll();
        }

        uint256 bal = want.balanceOf(address(this));
        if (bal > 0) {
            want.safeTransfer(address(lendersList[highest]), bal);
            lendersList[highest].deposit();
        }
    }

    /// @notice Function needed to inherit the baseStrategyUpgradeable
    function _adjustPosition(uint256) internal override {
        _adjustPosition();
    }

    /// @notice Withdraws a given amount from lenders
    /// @param _amount The amount to withdraw
    /// @dev Cycle through withdrawing from worst rate first
    function _withdrawSome(uint256 _amount) internal returns (uint256 amountWithdrawn) {
        IGenericLender[] memory lendersList = lenders;
        if (lendersList.length == 0) {
            return 0;
        }

        // Don't withdraw dust
        if (_amount < withdrawalThreshold) {
            return 0;
        }

        amountWithdrawn = 0;
        // In most situations this will only run once. Only big withdrawals will be a gas guzzler
        uint256 j = 0;
        while (amountWithdrawn < _amount - withdrawalThreshold) {
            uint256 lowestApr = type(uint256).max;
            uint256 lowest = 0;
            for (uint256 i = 0; i < lendersList.length; i++) {
                if (lendersList[i].hasAssets()) {
                    uint256 apr = lendersList[i].apr();
                    if (apr < lowestApr) {
                        lowestApr = apr;
                        lowest = i;
                    }
                }
            }
            if (!lendersList[lowest].hasAssets()) {
                return amountWithdrawn;
            }
            uint256 amountWithdrawnFromStrat = lendersList[lowest].withdraw(_amount - amountWithdrawn);
            // To avoid staying on the same strat if we can't withdraw anythin from it
            amountWithdrawn = amountWithdrawn + amountWithdrawnFromStrat;
            j++;
            // not best solution because it would be better to move to the 2nd lowestAPR instead of quiting
            if (amountWithdrawnFromStrat == 0) {
                return amountWithdrawn;
            }
            // To avoid want infinite loop
            if (j >= 6) {
                return amountWithdrawn;
            }
        }
    }

    /// @notice Liquidates up to `_amountNeeded` of `want` of this strategy's positions,
    /// irregardless of slippage. Any excess will be re-invested with `_adjustPosition()`.
    /// This function should return the amount of `want` tokens made available by the
    /// liquidation. If there is a difference between them, `_loss` indicates whether the
    /// difference is due to a realized loss, or if there is some other sitution at play
    /// (e.g. locked funds) where the amount made available is less than what is needed.
    ///
    /// NOTE: The invariant `_liquidatedAmount + _loss <= _amountNeeded` should always be maintained
    function _liquidatePosition(uint256 _amountNeeded) internal override returns (uint256 _amountFreed, uint256 _loss) {
        uint256 _balance = want.balanceOf(address(this));

        if (_balance >= _amountNeeded) {
            //if we don't set reserve here withdrawer will be sent our full balance
            return (_amountNeeded, 0);
        } else {
            uint256 received = _withdrawSome(_amountNeeded - _balance) + (_balance);
            if (received >= _amountNeeded) {
                return (_amountNeeded, 0);
            } else {
                return (received, 0);
            }
        }
    }

    /// @notice Liquidates everything and returns the amount that got freed.
    /// This function is used during emergency exit instead of `_prepareReturn()` to
    /// liquidate all of the Strategy's positions back to the Manager.
    function _liquidateAllPositions() internal override returns (uint256 _amountFreed) {
        (_amountFreed, ) = _liquidatePosition(estimatedTotalAssets());
    }

    // =============================== VIEW FUNCTIONS ==============================

    /// @notice View function to check the current state of the strategy
    /// @return Returns the status of all lenders attached the strategy
    function lendStatuses() external view returns (LendStatus[] memory) {
        uint256 lendersListLength = lenders.length;
        LendStatus[] memory statuses = new LendStatus[](lendersListLength);
        for (uint256 i = 0; i < lendersListLength; i++) {
            LendStatus memory s;
            s.name = lenders[i].lenderName();
            s.add = address(lenders[i]);
            s.assets = lenders[i].nav();
            s.rate = lenders[i].apr();
            statuses[i] = s;
        }
        return statuses;
    }

    /// @notice View function to check the total assets lent
    function lentTotalAssets() public view returns (uint256) {
        uint256 nav = 0;
        for (uint256 i = 0; i < lenders.length; i++) {
            nav = nav + lenders[i].nav();
        }
        return nav;
    }

    /// @notice View function to check the total assets managed by the strategy
    function estimatedTotalAssets() public view override returns (uint256 nav) {
        nav = lentTotalAssets() + want.balanceOf(address(this));
    }

    /// @notice View function to check the number of lending platforms
    function numLenders() external view returns (uint256) {
        return lenders.length;
    }

    /// @notice The weighted apr of all lenders. sum(nav * apr)/totalNav
    function estimatedAPR() external view returns (uint256) {
        uint256 bal = estimatedTotalAssets();
        if (bal == 0) {
            return 0;
        }

        uint256 weightedAPR = 0;

        for (uint256 i = 0; i < lenders.length; i++) {
            weightedAPR = weightedAPR + lenders[i].weightedApr();
        }

        return weightedAPR / bal;
    }

    /// @notice Prevents the governance from withdrawing want tokens
    function _protectedTokens() internal view override returns (address[] memory) {
        address[] memory protected = new address[](1);
        protected[0] = address(want);
        return protected;
    }

    // ================================= GOVERNANCE ================================

    struct LenderRatio {
        address lender;
        //share x 1000
        uint16 share;
    }

    /// @notice Reallocates all funds according to a new distribution
    /// @param _newPositions List of shares to specify the new allocation
    /// @dev Share must add up to 1000. 500 means 50% etc
    /// @dev This code has been forked, so we have not thoroughly tested it
    function manualAllocation(LenderRatio[] memory _newPositions) external onlyRole(GUARDIAN_ROLE) {
        IGenericLender[] memory lendersList = lenders;
        uint256 share = 0;
        for (uint256 i = 0; i < lendersList.length; i++) {
            lendersList[i].withdrawAll();
        }

        uint256 assets = want.balanceOf(address(this));

        for (uint256 i = 0; i < _newPositions.length; i++) {
            bool found = false;

            //might be annoying and expensive to do this second loop but worth it for safety
            for (uint256 j = 0; j < lendersList.length; j++) {
                if (address(lendersList[j]) == _newPositions[i].lender) {
                    found = true;
                }
            }
            require(found, "94");

            share = share + _newPositions[i].share;
            uint256 toSend = (assets * _newPositions[i].share) / 1000;
            want.safeTransfer(_newPositions[i].lender, toSend);
            IGenericLender(_newPositions[i].lender).deposit();
        }

        require(share == 1000, "95");
    }

    /// @notice Changes the withdrawal threshold
    /// @param _threshold The new withdrawal threshold
    /// @dev governor, guardian or `PoolManager` only
    function setWithdrawalThreshold(uint256 _threshold) external onlyRole(GUARDIAN_ROLE) {
        withdrawalThreshold = _threshold;
    }

    /// @notice Add lenders for the strategy to choose between
    /// @param newLender The adapter to the added lending platform
    /// @dev Governor, guardian or `PoolManager` only
    function addLender(IGenericLender newLender) external onlyRole(GUARDIAN_ROLE) {
        require(newLender.strategy() == address(this), "96");

        for (uint256 i = 0; i < lenders.length; i++) {
            require(address(newLender) != address(lenders[i]), "97");
        }
        lenders.push(newLender);

        emit AddLender(address(newLender));
    }

    /// @notice Removes a lending platform and fails if total withdrawal is impossible
    /// @param lender The address of the adapter to the lending platform to remove
    function safeRemoveLender(address lender) external onlyRole(GUARDIAN_ROLE) {
        _removeLender(lender, false);
    }

    /// @notice Removes a lending platform and even if total withdrawal is impossible
    /// @param lender The address of the adapter to the lending platform to remove
    function forceRemoveLender(address lender) external onlyRole(GUARDIAN_ROLE) {
        _removeLender(lender, true);
    }

    /// @notice Internal function to handle lending platform removing
    /// @param lender The address of the adapter for the lending platform to remove
    /// @param force Whether it is required that all the funds are withdrawn prior to removal
    function _removeLender(address lender, bool force) internal {
        IGenericLender[] memory lendersList = lenders;
        for (uint256 i = 0; i < lendersList.length; i++) {
            if (lender == address(lendersList[i])) {
                bool allWithdrawn = lendersList[i].withdrawAll();

                if (!force) {
                    require(allWithdrawn, "98");
                }

                // Put the last index here
                // then remove last index
                if (i != lendersList.length - 1) {
                    lenders[i] = lendersList[lendersList.length - 1];
                }

                // Pop shortens array by 1 thereby deleting the last index
                lenders.pop();

                // If balance to spend we might as well put it into the best lender
                if (want.balanceOf(address(this)) > 0) {
                    _adjustPosition();
                }

                emit RemoveLender(lender);

                return;
            }
        }
        require(false, "94");
    }

    // ============================= MANAGER FUNCTIONS =============================

    /// @notice Adds a new guardian address and echoes the change to the contracts
    /// that interact with this collateral `PoolManager`
    /// @param _guardian New guardian address
    /// @dev This internal function has to be put in this file because `AccessControl` is not defined
    /// in `PoolManagerInternal`
    function addGuardian(address _guardian) external override onlyRole(POOLMANAGER_ROLE) {
        // Granting the new role
        // Access control for this contract
        _grantRole(GUARDIAN_ROLE, _guardian);
        // Propagating the new role in other contract
        for (uint256 i = 0; i < lenders.length; i++) {
            lenders[i].grantRole(GUARDIAN_ROLE, _guardian);
        }
    }

    /// @notice Revokes the guardian role and propagates the change to other contracts
    /// @param guardian Old guardian address to revoke
    function revokeGuardian(address guardian) external override onlyRole(POOLMANAGER_ROLE) {
        _revokeRole(GUARDIAN_ROLE, guardian);
        for (uint256 i = 0; i < lenders.length; i++) {
            lenders[i].revokeRole(GUARDIAN_ROLE, guardian);
        }
    }
}

// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.12;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

import "../BaseStrategyUpgradeable.sol";

import "../../interfaces/IGenericLender.sol";

/// @title OptimizerAPRStrategy
/// @author Angle Labs, Inc.
/// @notice A lender optimisation strategy for any ERC20 asset, leveraging multiple lenders at once
/// @dev This strategy works by taking plugins designed for standard lending platforms and automatically
/// chooses to invest its funds in the best platforms to generate yield.
/// The allocation is greedy and may be sub-optimal so there is an additional option to manually set positions
contract OptimizerAPRStrategy is BaseStrategyUpgradeable {
    using SafeERC20 for IERC20;
    using Address for address;

    // ================================= CONSTANTS =================================

    uint64 internal constant _BPS = 10000;

    // ============================ CONTRACTS REFERENCES ===========================

    IGenericLender[] public lenders;

    // ================================= PARAMETERS ================================

    uint256 public withdrawalThreshold;

    // =================================== EVENTS ==================================

    event AddLender(address indexed lender);
    event RemoveLender(address indexed lender);

    /// @notice Constructor of the `Strategy`
    /// @param _poolManager Address of the `PoolManager` lending to this strategy
    /// @param governor Address with governor privilege
    /// @param guardian Address of the guardian
    function initialize(
        address _poolManager,
        address governor,
        address guardian,
        address[] memory keepers
    ) external {
        _initialize(_poolManager, governor, guardian, keepers);
        withdrawalThreshold = 1000 * wantBase;
    }

    // ============================= INTERNAL FUNCTIONS ============================

    /// @notice Frees up profit plus `_debtOutstanding`.
    /// @param _debtOutstanding Amount to withdraw
    /// @return _profit Profit freed by the call
    /// @return _loss Loss discovered by the call
    /// @return _debtPayment Amount freed to reimburse the debt
    /// @dev If `_debtOutstanding` is more than we can free we get as much as possible.
    function _prepareReturn(uint256 _debtOutstanding)
        internal
        override
        returns (
            uint256 _profit,
            uint256 _loss,
            uint256 _debtPayment
        )
    {
        _debtPayment = _debtOutstanding;

        uint256 lentAssets = lentTotalAssets();

        uint256 looseAssets = want.balanceOf(address(this));

        uint256 total = looseAssets + lentAssets;

        if (lentAssets == 0) {
            // No position to harvest or profit to report
            if (_debtPayment > looseAssets) {
                // We can only return looseAssets
                _debtPayment = looseAssets;
            }

            return (_profit, _loss, _debtPayment);
        }

        uint256 debt = poolManager.strategies(address(this)).totalStrategyDebt;

        if (total > debt) {
            _profit = total - debt;

            uint256 amountToFree = _profit + _debtPayment;
            // We need to add outstanding to our profit
            // don't need to do logic if there is nothing to free
            if (amountToFree != 0 && looseAssets < amountToFree) {
                // Withdraw what we can withdraw
                _withdrawSome(amountToFree - looseAssets);
                uint256 newLoose = want.balanceOf(address(this));

                // If we dont have enough money adjust _debtOutstanding and only change profit if needed
                if (newLoose < amountToFree) {
                    if (_profit > newLoose) {
                        _profit = newLoose;
                        _debtPayment = 0;
                    } else {
                        _debtPayment = Math.min(newLoose - _profit, _debtPayment);
                    }
                }
            }
        } else {
            // Serious loss should never happen but if it does lets record it accurately
            _loss = debt - total;

            uint256 amountToFree = _loss + _debtPayment;
            if (amountToFree != 0 && looseAssets < amountToFree) {
                // Withdraw what we can withdraw

                _withdrawSome(amountToFree - looseAssets);
                uint256 newLoose = want.balanceOf(address(this));

                // If we dont have enough money adjust `_debtOutstanding` and only change profit if needed
                if (newLoose < amountToFree) {
                    if (_loss > newLoose) {
                        _loss = newLoose;
                        _debtPayment = 0;
                    } else {
                        _debtPayment = Math.min(newLoose - _loss, _debtPayment);
                    }
                }
            }
        }
    }

    /// @notice Estimates highest and lowest apr lenders among a `lendersList`
    /// @param lendersList List of all the lender contracts associated to this strategy
    /// @return _lowest The index of the lender in the `lendersList` with lowest apr
    /// @return _highest The index of the lender with highest apr
    /// @return _investmentStrategy Whether we should invest from the lowest to the highest yielding strategy or simply invest loose assets
    /// @return _totalApr The APR computed according to (greedy) heuristics that will determine whether positions should be adjusted
    /// according to the solution proposed by the caller or according to the greedy method
    /// @dev `lendersList` is kept as a parameter to avoid multiplying reads in storage to the `lenders` array
    function _estimateGreedyAdjustPosition(IGenericLender[] memory lendersList)
        internal
        view
        returns (
            uint256 _lowest,
            uint256 _highest,
            bool _investmentStrategy,
            uint256 _totalApr
        )
    {
        // All loose assets are to be invested
        uint256 looseAssets = want.balanceOf(address(this));

        // Simple greedy algo:
        //  - Get the lowest apr strat
        //  - Cycle through and see who could take its funds to improve the overall highest APR
        uint256 lowestNav;
        uint256 highestApr;
        uint256 highestLenderNav;
        uint256 totalNav = looseAssets;
        uint256[] memory weightedAprs = new uint256[](lendersList.length);
        uint256 lendersListLength = lendersList.length;
        {
            uint256 lowestApr = type(uint256).max;
            for (uint256 i; i < lendersListLength; ++i) {
                uint256 aprAfterDeposit = lendersList[i].aprAfterDeposit(int256(looseAssets));
                uint256 nav = lendersList[i].nav();
                totalNav += nav;
                if (aprAfterDeposit > highestApr) {
                    highestApr = aprAfterDeposit;
                    highestLenderNav = nav;
                    _highest = i;
                }
                // Checking strategies that have assets
                if (nav > 10 * wantBase) {
                    uint256 apr = lendersList[i].apr();
                    weightedAprs[i] = apr * nav;
                    if (apr < lowestApr) {
                        lowestApr = apr;
                        lowestNav = nav;
                        _lowest = i;
                    }
                }
            }
        }

        // Comparing if we are better off removing from the lowest APR yielding strategy to invest in the highest or just invest
        // the loose assets in the highest yielding strategy
        if (totalNav > 0) {
            // Case where only loose assets are invested
            uint256 weightedApr1;
            // Case where funds are divested from the strategy with the lowest APR to be invested in the one with the highest APR
            uint256 weightedApr2;
            for (uint256 i; i < lendersListLength; ++i) {
                if (i == _highest) {
                    weightedApr1 += (highestLenderNav + looseAssets) * highestApr;
                    if (lowestNav != 0 && lendersListLength > 1)
                        weightedApr2 +=
                            (highestLenderNav + looseAssets + lowestNav) *
                            lendersList[_highest].aprAfterDeposit(int256(lowestNav + looseAssets));
                } else if (i == _lowest) {
                    weightedApr1 += weightedAprs[i];
                    // In the second case funds are divested so the lowest strat does not contribute to the highest APR case
                } else {
                    weightedApr1 += weightedAprs[i];
                    weightedApr2 += weightedAprs[i];
                }
            }
            if (weightedApr2 > weightedApr1 && lendersList.length > 1) {
                _investmentStrategy = true;
                _totalApr = weightedApr2 / totalNav;
            } else _totalApr = weightedApr1 / totalNav;
        }
    }

    /// @inheritdoc BaseStrategyUpgradeable
    function _adjustPosition(bytes memory data) internal override {
        // Emergency exit is dealt with at beginning of harvest
        if (emergencyExit) return;

        // Storing the `lenders` array in a cache variable
        IGenericLender[] memory lendersList = lenders;
        uint256 lendersListLength = lendersList.length;
        // We just keep all money in `want` if we dont have any lenders
        if (lendersListLength == 0) return;

        uint64[] memory lenderSharesHint = abi.decode(data, (uint64[]));

        uint256 estimatedAprHint;
        int256[] memory lenderAdjustedAmounts;
        if (lenderSharesHint.length != 0) (estimatedAprHint, lenderAdjustedAmounts) = estimatedAPR(lenderSharesHint);
        (uint256 lowest, uint256 highest, bool _investmentStrategy, uint256 _totalApr) = _estimateGreedyAdjustPosition(
            lendersList
        );

        // The hint was successful --> we find a better allocation than the current one
        if (_totalApr < estimatedAprHint) {
            uint256 deltaWithdraw;
            for (uint256 i; i < lendersListLength; ++i) {
                if (lenderAdjustedAmounts[i] < 0) {
                    deltaWithdraw +=
                        uint256(-lenderAdjustedAmounts[i]) -
                        lendersList[i].withdraw(uint256(-lenderAdjustedAmounts[i]));
                }
            }

            // If the strategy didn't succeed to withdraw the intended funds -> revert and force the greedy path
            if (deltaWithdraw > withdrawalThreshold) revert IncorrectDistribution();

            for (uint256 i; i < lendersListLength; ++i) {
                // As `deltaWithdraw` is inferior to `withdrawalThreshold` (a dust)
                // It is not critical to compensate on an arbitrary lender as it will only slightly impact global APR
                if (lenderAdjustedAmounts[i] > int256(deltaWithdraw)) {
                    lenderAdjustedAmounts[i] -= int256(deltaWithdraw);
                    deltaWithdraw = 0;
                    want.safeTransfer(address(lendersList[i]), uint256(lenderAdjustedAmounts[i]));
                    lendersList[i].deposit();
                } else if (lenderAdjustedAmounts[i] > 0) deltaWithdraw -= uint256(lenderAdjustedAmounts[i]);
            }
        } else {
            if (_investmentStrategy) {
                lendersList[lowest].withdrawAll();
            }

            uint256 bal = want.balanceOf(address(this));
            if (bal != 0) {
                want.safeTransfer(address(lendersList[highest]), bal);
                lendersList[highest].deposit();
            }
        }
    }

    /// @inheritdoc BaseStrategyUpgradeable
    function _adjustPosition() internal override {
        _adjustPosition(abi.encode(new uint64[](0)));
    }

    /// @inheritdoc BaseStrategyUpgradeable
    function _adjustPosition(uint256) internal override {
        _adjustPosition(abi.encode(new uint64[](0)));
    }

    /// @notice Withdraws a given amount from lenders
    /// @param _amount The amount to withdraw
    /// @dev Cycle through withdrawing from worst rate first
    function _withdrawSome(uint256 _amount) internal returns (uint256 amountWithdrawn) {
        IGenericLender[] memory lendersList = lenders;
        uint256 lendersListLength = lendersList.length;
        if (lendersListLength == 0) {
            return 0;
        }

        // Don't withdraw dust
        uint256 _withdrawalThreshold = withdrawalThreshold;
        if (_amount < _withdrawalThreshold) {
            return 0;
        }

        amountWithdrawn;
        // In most situations this will only run once. Only big withdrawals will be a gas guzzler
        uint256 j;
        while (amountWithdrawn < _amount - _withdrawalThreshold) {
            uint256 lowestApr = type(uint256).max;
            uint256 lowest;
            for (uint256 i; i < lendersListLength; ++i) {
                if (lendersList[i].hasAssets()) {
                    uint256 apr = lendersList[i].apr();
                    if (apr < lowestApr) {
                        lowestApr = apr;
                        lowest = i;
                    }
                }
            }
            if (!lendersList[lowest].hasAssets()) {
                return amountWithdrawn;
            }
            uint256 amountWithdrawnFromStrat = lendersList[lowest].withdraw(_amount - amountWithdrawn);
            // To avoid staying on the same strat if we can't withdraw anythin from it
            amountWithdrawn += amountWithdrawnFromStrat;
            ++j;
            // not best solution because it would be better to move to the 2nd lowestAPR instead of quiting
            if (amountWithdrawnFromStrat == 0) {
                return amountWithdrawn;
            }
            // To avoid want infinite loop
            if (j >= 6) {
                return amountWithdrawn;
            }
        }
    }

    /// @notice Liquidates up to `_amountNeeded` of `want` of this strategy's positions,
    /// irregardless of slippage. Any excess will be re-invested with `_adjustPosition()`.
    /// This function should return the amount of `want` tokens made available by the
    /// liquidation. If there is a difference between them, `_loss` indicates whether the
    /// difference is due to a realized loss, or if there is some other sitution at play
    /// (e.g. locked funds) where the amount made available is less than what is needed.
    ///
    /// NOTE: The invariant `_liquidatedAmount + _loss <= _amountNeeded` should always be maintained
    function _liquidatePosition(uint256 _amountNeeded) internal override returns (uint256 _amountFreed, uint256 _loss) {
        uint256 _balance = want.balanceOf(address(this));

        if (_balance >= _amountNeeded) {
            //if we don't set reserve here withdrawer will be sent our full balance
            return (_amountNeeded, 0);
        } else {
            uint256 received = _withdrawSome(_amountNeeded - _balance) + (_balance);
            if (received >= _amountNeeded) {
                return (_amountNeeded, 0);
            } else {
                return (received, 0);
            }
        }
    }

    /// @notice Liquidates everything and returns the amount that got freed.
    /// This function is used during emergency exit instead of `_prepareReturn()` to
    /// liquidate all of the Strategy's positions back to the Manager.
    function _liquidateAllPositions() internal override returns (uint256 _amountFreed) {
        (_amountFreed, ) = _liquidatePosition(estimatedTotalAssets());
    }

    // =============================== VIEW FUNCTIONS ==============================

    /// @notice View function to check the current state of the strategy
    /// @return Returns the status of all lenders attached the strategy
    function lendStatuses() external view returns (LendStatus[] memory) {
        uint256 lendersLength = lenders.length;
        LendStatus[] memory statuses = new LendStatus[](lendersLength);
        for (uint256 i; i < lendersLength; ++i) {
            LendStatus memory s;
            s.name = lenders[i].lenderName();
            s.add = address(lenders[i]);
            s.assets = lenders[i].nav();
            s.rate = lenders[i].apr();
            statuses[i] = s;
        }
        return statuses;
    }

    /// @notice View function to check the total assets lent
    function lentTotalAssets() public view returns (uint256) {
        uint256 nav;
        uint256 lendersLength = lenders.length;
        for (uint256 i; i < lendersLength; ++i) {
            nav += lenders[i].nav();
        }
        return nav;
    }

    /// @notice View function to check the total assets managed by the strategy
    function estimatedTotalAssets() public view override returns (uint256 nav) {
        nav = lentTotalAssets() + want.balanceOf(address(this));
    }

    /// @notice View function to check the number of lending platforms
    function numLenders() external view returns (uint256) {
        return lenders.length;
    }

    /// @notice Returns the weighted apr of all lenders
    /// @dev It's computed by doing: `sum(nav * apr) / totalNav`
    function estimatedAPR() external view returns (uint256) {
        uint256 bal = estimatedTotalAssets();
        if (bal == 0) {
            return 0;
        }

        uint256 weightedAPR;
        uint256 lendersLength = lenders.length;
        for (uint256 i; i < lendersLength; ++i) {
            weightedAPR += lenders[i].weightedApr();
        }

        return weightedAPR / bal;
    }

    /// @notice Returns the weighted apr in an hypothetical world where the strategy splits its nav
    /// in respect to shares
    /// @param shares List of shares (in bps of the nav) that should be allocated to each lender
    function estimatedAPR(uint64[] memory shares)
        public
        view
        returns (uint256 weightedAPR, int256[] memory lenderAdjustedAmounts)
    {
        uint256 lenderLength = lenders.length;
        lenderAdjustedAmounts = new int256[](lenderLength);
        if (lenderLength != shares.length) revert IncorrectListLength();

        uint256 bal = estimatedTotalAssets();
        if (bal == 0) return (weightedAPR, lenderAdjustedAmounts);

        uint256 share;
        for (uint256 i; i < lenderLength; ++i) {
            share += shares[i];
            uint256 futureDeposit = (bal * shares[i]) / _BPS;
            // It won't overflow for `decimals <= 18`, as it would mean gigantic amounts
            int256 adjustedAmount = int256(futureDeposit) - int256(lenders[i].nav());
            lenderAdjustedAmounts[i] = adjustedAmount;
            weightedAPR += futureDeposit * lenders[i].aprAfterDeposit(adjustedAmount);
        }
        if (share != 10000) revert InvalidShares();

        weightedAPR /= bal;
    }

    /// @notice Prevents governance from withdrawing `want` tokens
    function _protectedTokens() internal view override returns (address[] memory) {
        address[] memory protected = new address[](1);
        protected[0] = address(want);
        return protected;
    }

    // ================================= GOVERNANCE ================================

    /// @notice Changes the withdrawal threshold
    /// @param _threshold New withdrawal threshold
    /// @dev governor, guardian or `PoolManager` only
    function setWithdrawalThreshold(uint256 _threshold) external onlyRole(GUARDIAN_ROLE) {
        withdrawalThreshold = _threshold;
    }

    /// @notice Add lenders for the strategy to choose between
    /// @param newLender The adapter to the added lending platform
    /// @dev Governor, guardian or `PoolManager` only
    function addLender(IGenericLender newLender) external onlyRole(GUARDIAN_ROLE) {
        if (newLender.strategy() != address(this)) revert UndockedLender();
        uint256 lendersLength = lenders.length;
        for (uint256 i; i < lendersLength; ++i) {
            if (address(newLender) == address(lenders[i])) revert LenderAlreadyAdded();
        }
        lenders.push(newLender);

        emit AddLender(address(newLender));
    }

    /// @notice Removes a lending platform and fails if total withdrawal is impossible
    /// @param lender The address of the adapter to the lending platform to remove
    function safeRemoveLender(address lender) external onlyRole(KEEPER_ROLE) {
        _removeLender(lender, false);
    }

    /// @notice Removes a lending platform even if total withdrawal is impossible
    /// @param lender The address of the adapter to the lending platform to remove
    function forceRemoveLender(address lender) external onlyRole(GUARDIAN_ROLE) {
        _removeLender(lender, true);
    }

    /// @notice Internal function to handle lending platform removal
    /// @param lender The address of the adapter for the lending platform to remove
    /// @param force Whether it is required that all the funds are withdrawn prior to removal
    function _removeLender(address lender, bool force) internal {
        IGenericLender[] memory lendersList = lenders;
        uint256 lendersListLength = lendersList.length;
        for (uint256 i; i < lendersListLength; ++i) {
            if (lender == address(lendersList[i])) {
                bool allWithdrawn = lendersList[i].withdrawAll();

                if (!force && !allWithdrawn) revert FailedWithdrawal();

                // Put the last index here
                // then remove last index
                if (i != lendersListLength - 1) {
                    lenders[i] = lendersList[lendersListLength - 1];
                }

                // Pop shortens array by 1 thereby deleting the last index
                lenders.pop();

                // If balance to spend we might as well put it into the best lender
                if (want.balanceOf(address(this)) != 0) {
                    _adjustPosition();
                }

                emit RemoveLender(lender);

                return;
            }
        }
        revert NonExistentLender();
    }

    // ============================= MANAGER FUNCTIONS =============================

    /// @notice Adds a new guardian address and echoes the change to the contracts
    /// that interact with this collateral `PoolManager`
    /// @param _guardian New guardian address
    /// @dev This internal function has to be put in this file because `AccessControl` is not defined
    /// in `PoolManagerInternal`
    function addGuardian(address _guardian) external override onlyRole(POOLMANAGER_ROLE) {
        // Granting the new role
        // Access control for this contract
        _grantRole(GUARDIAN_ROLE, _guardian);
        // Propagating the new role to underyling lenders
        uint256 lendersLength = lenders.length;
        for (uint256 i; i < lendersLength; ++i) {
            lenders[i].grantRole(GUARDIAN_ROLE, _guardian);
        }
    }

    /// @notice Revokes the guardian role and propagates the change to other contracts
    /// @param guardian Old guardian address to revoke
    function revokeGuardian(address guardian) external override onlyRole(POOLMANAGER_ROLE) {
        _revokeRole(GUARDIAN_ROLE, guardian);
        uint256 lendersLength = lenders.length;
        for (uint256 i; i < lendersLength; ++i) {
            lenders[i].revokeRole(GUARDIAN_ROLE, guardian);
        }
    }
}

contracts/strategies/StETHStrategy/StETH.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.12;

import "../../interfaces/external/curve/IStableSwapPool.sol";
import "../../interfaces/external/lido/ISteth.sol";
import "../../interfaces/external/IWETH9.sol";
import "../BaseStrategyUpgradeable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/Address.sol";

/// @title StrategyStETHAcc
/// @author Forked from https://github.com/Grandthrax/yearn-steth-acc/blob/master/contracts/Strategy.sol
/// @notice A strategy designed to getting yield on wETH by putting ETH in Lido or Curve for stETH and exiting
/// for wETH
contract StETHStrategy is BaseStrategyUpgradeable {
    using SafeERC20 for IERC20;
    using Address for address;

    /// @notice Current `apr` of the strategy: this apr needs to be manually filled by the strategist
    /// and updated when Lido's APR changes. It is put like that as there is no easy way to compute Lido's APR
    /// on-chain
    /// @notice Base used is `BASE_PARAMS`
    uint256 public apr;

    /// @notice Reference to the Curve ETH/stETH
    IStableSwapPool public stableSwapSTETH;
    /// @notice Reference to wETH, it should normally be equal to `want`
    IWETH9 public weth;
    /// @notice Reference to the stETH token
    ISteth public stETH;

    address private _referral = 0xdC4e6DFe07EFCa50a197DF15D9200883eF4Eb1c8; //stratms. for recycling and redepositing
    /// @notice Maximum trade size within the strategy
    uint256 public maxSingleTrade;
    /// @notice Parameter used for slippage protection
    uint256 public constant DENOMINATOR = 10_000;
    /// @notice Slippage parameter for the swaps on Curve: out of `DENOMINATOR`
    uint256 public slippageProtectionOut; // = 50; //out of 10000. 50 = 0.5%

    /// @notice ID of ETH in the Curve pool
    int128 private constant _WETHID = 0;
    /// @notice ID of stETH in the Curve pool
    int128 private constant _STETHID = 1;

    /// @notice Constructor of the `Strategy`
    /// @param _poolManager Address of the `PoolManager` lending to this strategy
    /// @param governor Address of the governance multisig with governor privilege
    /// @param guardian Address of the guardian
    /// @param keepers List of addresses of keepers
    /// @param _stableSwapSTETH Address of the stETH/ETH Curve pool
    /// @param _weth Address of wETH
    /// @param _stETH Address of the stETH token
    /// @param _apr Estimated apr on staked ETH
    function initialize(
        address _poolManager,
        address governor,
        address guardian,
        address[] memory keepers,
        address _stableSwapSTETH,
        address _weth,
        ISteth _stETH,
        uint256 _apr
    ) external {
        _initialize(_poolManager, governor, guardian, keepers);
        require(address(want) == _weth, "20");
        stableSwapSTETH = IStableSwapPool(_stableSwapSTETH);
        weth = IWETH9(_weth);
        stETH = ISteth(_stETH);
        apr = _apr;
        _stETH.approve(_stableSwapSTETH, type(uint256).max);
        maxSingleTrade = 10_000 * 1e18;
        slippageProtectionOut = 30;
    }

    /// @notice This contract gets ETH and so it needs this function
    receive() external payable {}

    // ========================== View Functions ===================================

    /// @notice View function to check the total assets managed by the strategy
    /// @dev We are purposely treating stETH and ETH as being equivalent.
    /// This is for a few reasons. The main one is that we do not have a good way to value
    /// stETH at any current time without creating exploit routes.
    /// Currently you can mint eth for steth but can't burn steth for eth so need to sell.
    /// Once eth 2.0 is merged you will be able to burn 1-1 as well.
    /// The main downside here is that we will noramlly overvalue our position as we expect stETH
    /// to trade slightly below peg. That means we will earn profit on deposits and take losses on withdrawals.
    /// This may sound scary but it is the equivalent of using virtualprice in a curve lp.
    /// As we have seen from many exploits, virtual pricing is safer than touch pricing.
    function estimatedTotalAssets() public view override returns (uint256) {
        return stethBalance() + wantBalance();
    }

    /// @notice Returns the wETH balance of the strategy
    function wantBalance() public view returns (uint256) {
        return want.balanceOf(address(this));
    }

    /// @notice Returns the stETH balance of the strategy
    function stethBalance() public view returns (uint256) {
        return stETH.balanceOf(address(this));
    }

    /// @notice The ETH APR of owning stETH
    function estimatedAPR() external view returns (uint256) {
        return apr;
    }

    // ========================== Strategy Functions ===============================

    /// @notice Frees up profit plus `_debtOutstanding`.
    /// @param _debtOutstanding Amount to withdraw
    /// @return _profit Profit freed by the call
    /// @return _loss Loss discovered by the call
    /// @return _debtPayment Amount freed to reimburse the debt: it is an amount made available for the `PoolManager`
    /// @dev If `_debtOutstanding` is more than we can free we get as much as possible.
    function _prepareReturn(uint256 _debtOutstanding)
        internal
        override
        returns (
            uint256 _profit,
            uint256 _loss,
            uint256 _debtPayment
        )
    {
        uint256 wantBal = wantBalance();
        uint256 stethBal = stethBalance();
        uint256 totalAssets = wantBal + stethBal;

        uint256 debt = poolManager.strategies(address(this)).totalStrategyDebt;

        if (totalAssets >= debt) {
            _profit = totalAssets - debt;

            uint256 toWithdraw = _profit + _debtOutstanding;
            // If more should be withdrawn than what's in the strategy: we divest from Curve
            if (toWithdraw > wantBal) {
                // We step our withdrawals. Adjust max single trade to withdraw more
                uint256 willWithdraw = Math.min(maxSingleTrade, toWithdraw - wantBal);
                uint256 withdrawn = _divest(willWithdraw);
                if (withdrawn < willWithdraw) {
                    _loss = willWithdraw - withdrawn;
                }
            }
            wantBal = wantBalance();

            // Computing net off profit and loss
            if (_profit >= _loss) {
                _profit = _profit - _loss;
                _loss = 0;
            } else {
                _profit = 0;
                _loss = _loss - _profit;
            }

            // profit + _debtOutstanding must be <= wantbalance. Prioritise profit first
            if (wantBal < _profit) {
                _profit = wantBal;
            } else if (wantBal < toWithdraw) {
                _debtPayment = wantBal - _profit;
            } else {
                _debtPayment = _debtOutstanding;
            }
        } else {
            _loss = debt - totalAssets;
        }
    }

    /// @notice Liquidates everything and returns the amount that got freed.
    /// This function is used during emergency exit instead of `_prepareReturn()` to
    /// liquidate all of the Strategy's positions back to the Manager.
    function _liquidateAllPositions() internal override returns (uint256 _amountFreed) {
        _divest(stethBalance());
        _amountFreed = wantBalance();
    }

    /// @notice Function called when harvesting to invest in stETH
    /// Function used in other contracts, in this strategy it is useless
    function _adjustPosition(uint256) internal override {
        _adjustPosition();
    }

    /// @notice Function called when harvesting to invest in stETH
    function _adjustPosition() internal override {
        uint256 toInvest = wantBalance();
        if (toInvest > 0) {
            uint256 realInvest = Math.min(maxSingleTrade, toInvest);
            _invest(realInvest);
        }
    }

    /// @notice Invests `_amount` wETH in stETH
    /// @param _amount Amount of wETH to put in stETH
    /// @return The amount of stETH received from the investment
    /// @dev This function chooses the optimal route between going to Lido directly or doing a swap on Curve
    /// @dev This function automatically wraps wETH to ETH
    function _invest(uint256 _amount) internal returns (uint256) {
        uint256 before = stethBalance();
        // Unwrapping the tokens
        weth.withdraw(_amount);
        // Test if we should buy from Curve instead of minting from Lido
        uint256 out = stableSwapSTETH.get_dy(_WETHID, _STETHID, _amount);
        if (out < _amount) {
            // If we get less than one stETH per wETH we use Lido
            stETH.submit{ value: _amount }(_referral);
        } else {
            // Otherwise, we do a Curve swap
            stableSwapSTETH.exchange{ value: _amount }(_WETHID, _STETHID, _amount, _amount);
        }

        return stethBalance() - before;
    }

    /// @notice Divests stETH on Curve and gets wETH back to the strategy in exchange
    /// @param _amount Amount of stETH to divest
    /// @dev Curve is the only place to convert stETH to ETH
    function _divest(uint256 _amount) internal returns (uint256) {
        uint256 before = wantBalance();

        // Computing slippage protection for the swap
        uint256 slippageAllowance = (_amount * (DENOMINATOR - slippageProtectionOut)) / DENOMINATOR;
        // Curve swap
        stableSwapSTETH.exchange(_STETHID, _WETHID, _amount, slippageAllowance);

        weth.deposit{ value: address(this).balance }();

        return wantBalance() - before;
    }

    /// @notice Attempts to withdraw `_amountNeeded` from the strategy and lets the user decide if they take the loss or not
    /// @param _amountNeeded Amount to withdraw from the strategy
    function _liquidatePosition(uint256 _amountNeeded)
        internal
        override
        returns (uint256 _liquidatedAmount, uint256 _loss)
    {
        uint256 wantBal = wantBalance();
        if (wantBal < _amountNeeded) {
            uint256 toWithdraw = _amountNeeded - wantBal;
            uint256 withdrawn = _divest(toWithdraw);
            if (withdrawn < toWithdraw) {
                _loss = toWithdraw - withdrawn;
            }
        }

        _liquidatedAmount = _amountNeeded - _loss;
    }

    // NOTE: Can override `tendTrigger` and `harvestTrigger` if necessary

    // Override this to add all tokens/tokenized positions this contract manages
    // on a *persistent* basis (e.g. not just for swapping back to want ephemerally)
    // NOTE: Do *not* include `want`, already included in `sweep` below
    //
    // Example:
    //
    //    function _protectedTokens() internal override view returns (address[] memory) {
    //      address[] memory protected = new address[](3);
    //      protected[0] = tokenA;
    //      protected[1] = tokenB;
    //      protected[2] = tokenC;
    //      return protected;
    //    }
    function _protectedTokens() internal view override returns (address[] memory) {
        address[] memory protected = new address[](1);
        protected[0] = address(stETH);

        return protected;
    }

    // ============================ Governance =====================================

    /// @notice Updates the referral code for Lido
    /// @param newReferral Address of the new referral
    function updateReferral(address newReferral) public onlyRole(GUARDIAN_ROLE) {
        _referral = newReferral;
    }

    /// @notice Updates the size of a trade in the strategy
    /// @param _maxSingleTrade New `maxSingleTrade` value
    function updateMaxSingleTrade(uint256 _maxSingleTrade) public onlyRole(GUARDIAN_ROLE) {
        maxSingleTrade = _maxSingleTrade;
    }

    /// @notice Changes the estimated APR of the strategy
    /// @param _apr New strategy APR
    function setApr(uint256 _apr) public onlyRole(GUARDIAN_ROLE) {
        apr = _apr;
    }

    /// @notice Updates the maximum slippage protection parameter
    /// @param _slippageProtectionOut New slippage protection parameter
    function updateSlippageProtectionOut(uint256 _slippageProtectionOut) public onlyRole(GUARDIAN_ROLE) {
        slippageProtectionOut = _slippageProtectionOut;
    }

    /// @notice Invests `_amount` in stETH
    /// @param _amount Amount to invest
    /// @dev This function allows to override the behavior that could be obtained through `harvest` calls
    function invest(uint256 _amount) external onlyRole(GUARDIAN_ROLE) {
        require(wantBalance() >= _amount);
        uint256 realInvest = Math.min(maxSingleTrade, _amount);
        _invest(realInvest);
    }

    /// @notice Rescues stuck ETH from the strategy
    /// @dev This strategy should never have stuck eth, but let it just in case
    function rescueStuckEth() external onlyRole(GUARDIAN_ROLE) {
        weth.deposit{ value: address(this).balance }();
    }

    // ========================== Manager functions ================================

    /// @notice Adds a new guardian address and echoes the change to the contracts
    /// that interact with this collateral `PoolManager`
    /// @param _guardian New guardian address
    /// @dev This internal function has to be put in this file because `AccessControl` is not defined
    /// in `PoolManagerInternal`
    function addGuardian(address _guardian) external override onlyRole(POOLMANAGER_ROLE) {
        // Granting the new role
        // Access control for this contract
        _grantRole(GUARDIAN_ROLE, _guardian);
    }

    /// @notice Revokes the guardian role and propagates the change to other contracts
    /// @param guardian Old guardian address to revoke
    function revokeGuardian(address guardian) external override onlyRole(POOLMANAGER_ROLE) {
        _revokeRole(GUARDIAN_ROLE, guardian);
    }
}

